import React, { useState } from 'react'
import axios from 'axios'

import SearchComponent from './components/SearchComponent'
import ResultsComponent from './components/ResultsComponent'
import PopupComponent from './components/PopupComponent'

function App() {
  const [state, setState] = useState({
    s: "",
    results: [],
    selected: {}
  });
  const apiurl = "http://www.omdbapi.com/?apikey=dfe6d885";

  const search = (e) => {
    if (e.key === "Enter") {
      axios(apiurl + "&s=" + state.s).then(({ data }) => {
        let results = data.Search;

        setState(prevState => {
          return { ...prevState, results: results }
        })
      });
    }
  }

  const handleInput = (e) => {
    let s = e.target.value;

    setState(prevState => {
      return { ...prevState, s: s }
    });
  }

  const openPopup = id => {
    axios(apiurl + "&i=" + id).then(({ data }) => {
      let result = data;

      console.log(result);

      setState(prevState => {
        return { ...prevState, selected: result }
      });
    });
  }

  const closePopup = () => {
    setState(prevState => {
      return { ...prevState, selected: {} }
    });
  }

  return (
    <div className="App">
      <header>
        <h1>Search For The Movie </h1>
      </header>
      <main>
        <SearchComponent handleInput={handleInput} search={search} />

        <ResultsComponent results={state.results} openPopup={openPopup} />

        {(typeof state.selected.Title != "undefined") ? <PopupComponent selected={state.selected} closePopup={closePopup} /> : false}
      </main>
    </div>
  );
}

export default App
