import React, { useState } from "react";
import SearchComponent from "./components/SearchComponent";

function App() {
  const [state, setState] = {
    s: "",
    results: [],
    selected: {},
  };

  const apiURL = " http://www.omdbapi.com/?i=tt3896198&apikey=1b366ad";

  const handleInput = (e) => {
    let s = e.target.value;

    setState((prevState) => {
      return { ...prevState, s: s };
    });
    console.log(state.s);
  };

  return (
    <div className="App">
      <header>
        <h1>Movie Database</h1>
      </header>
      <main>
        <SearchComponent handleInput />
      </main>
    </div>
  );
}

export default App;
