import React, { useState } from "react";
import axios from 'axios';
import SearchComponent from "./components/SearchComponent";

function App() {
  const [state, setState] = useState({
    s: "",
    results: [],
    selected: {}
  });

  const apiURL = " http://www.omdbapi.com/?i=tt3896198&apikey=1b366ad";

  const search =(e) => {
    if (e.key === "Enter") {
      axios(apiurl + "&")
    }
  }

  const handleInput = (e) => {
    let s = e.target.value;

    setState((prevState) => {
      return { ...prevState, s: s };
    });
    console.log(state.s);
  };

  return (
    <div className="App">
      <header>
        <h1>Movie Database</h1>
      </header>
      <main>
        <SearchComponent handleInput = {handleInput} />
      </main>
    </div>
  );
}

export default App;
